<?php class gJSON{static public function parse(string $text):array{$array=json_decode($text,true);return $array;}static public function stringify(mixed $object):string{$string=json_encode($object);return $string;}static public function parseable(string $text):bool{try{gJSON::parse($text);return true;}catch(\Throwable $th){return false;}}static public function flatten(mixed $object,string $prev=''):array{$formatted=array();foreach($object as $key=>$value){$type=gettype($value);if($type=='array'){$prev_key=$prev?"$prev.$key":$key;$object2=gJSON::flatten($value,$prev_key);foreach($object2 as $key2=>$value2){$formatted[$key2]=$value2;}}else{$prev_key=$prev?"$prev.":'';$formatted["$prev_key$key"]=$value;}}return $formatted;}}